mapper.insert:
INSERT INTO GOODS_INFO /* registerGoods */
            (
            CATEGORY_NO
            , SEASON
            , GOODS_NO
            , GOODS_NM
            , REP_COLOR
            , SIZE_GROUP_NO
            , GOODS_DESC
            , REGR
            , REG_DT
            )
     VALUES
            (
            #{categoryNo}
            , #{season}
            , #{goodsNo}
            , #{goodsNm}
            , #{repColor}
            , #{sizeGroupNo}
            , #{goodsDesc}
            , #{regr}
            , NOW()
            )

INSERT INTO GOODS_DETAIL /* registerGoodsDetail */
            (
            GOODS_NO
            , COLOR
            , SIZE
            , COLOR_ORD
            , SIZE_ORD
            , SUPPLY_PRICE
            , RETAIL_PRICE
            , ACTUAL_PRICE
            , REGR
            , REG_DT
            )
     VALUES

            (
            #{detail.goodsNo}
            , #{detail.color}
            , #{detail.size}
            , #{detail.colorOrd}
            , #{detail.sizeOrd}
            , #{detail.supplyPrice}
            , #{detail.retailPrice}
            , #{detail.actualPrice}
            , #{detail.regr}
            , NOW()
            )
