mapper.insert:
INSERT INTO GOODS_INFO /* registerGoods */
            (
            CATEGORY_NO
            , SEASON
            , GOODS_NO
            , GOODS_NM
            , SIZE_GROUP_NO
            , GOODS_DESC
            , REGR
            , REG_DT
            )
     VALUES
            (
            #{categoryNo}
            , #{season}
            , #{goodsNo}
            , #{goodsNm}
            , #{sizeGroupNo}
            , #{goodsDesc}
            , #{regr}
            , NOW()
            )

INSERT INTO GOODS_DETAIL /* registerGoodsDetail */
            (
            GOODS_NO
            , COLOR
            , SIZE
            , COLOR_ORD
            , SIZE_ORD
            , SUPPLY_PRICE
            , RETAIL_PRICE
            , ACTUAL_PRICE
            , REGR
            , REG_DT
            )
     VALUES

            (
            #{detail.goodsNo}
            , #{detail.color}
            , #{detail.size}
            , #{detail.colorOrd}
            , #{detail.sizeOrd}
            , #{detail.supplyPrice}
            , #{detail.retailPrice}
            , #{detail.actualPrice}
            , #{detail.regr}
            , NOW()
            )

mapper.update:
UPDATE GOODS_INFO
   SET
       SEASON = #{season}
       , CATEGORY_NO = #{categoryNo}

       , SIZE_GROUP_NO = #{sizeGroupNo}

       , UPDR = #{updr}
       , UPD_DT = NOW()
 WHERE GOODS_NO = #{goodsNo}

UPDATE GOODS_DETAIL
   SET
       SUPPLY_PRICE = #{detail.supplyPrice}
       , RETAIL_PRICE = #{detail.retailPrice}
       , UPDR = #{updr}
       , UPD_DT = NOW()
 WHERE GOODS_NO = #{goodsNo}
